package com.google.cloud.healthcare.fdamystudies.controller;

import com.google.cloud.WriteChannel;
import com.google.cloud.healthcare.fdamystudies.bean.ErrorBean;
import com.google.cloud.storage.Blob;
import com.google.cloud.storage.BlobId;
import com.google.cloud.storage.BlobInfo;
import com.google.cloud.storage.Storage;
import com.google.cloud.storage.StorageOptions;
import java.io.ByteArrayOutputStream;
import java.io.IOException;
import java.nio.ByteBuffer;
import org.slf4j.ext.XLogger;
import org.slf4j.ext.XLoggerFactory;
import org.springframework.http.HttpStatus;
import org.springframework.http.ResponseEntity;
import org.springframework.web.bind.annotation.GetMapping;
import org.springframework.web.bind.annotation.PostMapping;
import org.springframework.web.bind.annotation.RequestHeader;
import org.springframework.web.bind.annotation.RestController;

@RestController
public class TestPDFUpload {
  private XLogger logger = XLoggerFactory.getXLogger(TestPDFUpload.class.getName());

  @PostMapping(
      value = "/testPDFUpload",
      consumes = "application/json",
      produces = "application/json")
  public ResponseEntity<?> testPDFUpload(@RequestHeader String fileName) {
    ErrorBean errorBean = null;
    String sampleBase64 =
        "";
    String bucketName = "consent-test-pdf";

    logger.entry(
        "UserConsentManagementController testPDFUpload() - starts with bucket : "
            + bucketName
            + " and filename : "
            + fileName);

    try {
      logger.info("first try block starts in testPDFUpload");

      Storage storageService = StorageOptions.getDefaultInstance().getService();
      BlobInfo blobInfo = BlobInfo.newBuilder(bucketName, fileName).build();
      byte[] bytes = null;
      logger.info("storageService created : " + storageService);
      logger.info("blobInfo created : " + blobInfo);

      try (WriteChannel writer = storageService.writer(blobInfo)) {
        logger.info(" second try block starts");
        logger.info("writer created : " + writer);
        bytes = sampleBase64.getBytes();
        logger.info("bytes created : " + bytes);

        writer.write(ByteBuffer.wrap(bytes, 0, bytes.length));
        logger.info("second try block ends ");

      } catch (IOException e) {
        logger.info("Inside second IOException catch block ");

        logger.info("UserConsentManagementController testPDFUpload() - error IOException ", e);

        throw new RuntimeException(e);
      }
      logger.info("first try block ends");

    } catch (Exception e) {
      logger.info("Inside second Exception catch block ");

      logger.info("UserConsentManagementController testPDFUpload() - error ", e);
    }
    logger.exit("UserConsentManagementController testPDFUpload() - ends ");

    return new ResponseEntity<>(errorBean, HttpStatus.OK);
  }

  @GetMapping(
      value = "/testPDFDownload",
      consumes = "application/json",
      produces = "application/json")
  public ResponseEntity<?> testPDFDownload(@RequestHeader String fileName) {
    logger.entry("UserConsentManagementController testPDFDownload() - starts ");
    ErrorBean errorBean = null;

    String bucketName = "consent-test-pdf";

    String fileData = "";
    try {
      logger.info("first try block starts ");

      Storage storageService = StorageOptions.getDefaultInstance().getService();
      logger.info("storageService created :" + storageService);

      try {
        logger.info("first try block starts ");

        ByteArrayOutputStream baos = new ByteArrayOutputStream();
        logger.info("baos created :" + baos);

        Blob blob = storageService.get(BlobId.of(bucketName, fileName));
        logger.info("blob created :" + blob);

        blob.downloadTo(baos);
        fileData = new String(baos.toByteArray());
        logger.info("fileData created :" + fileData);
        logger.info("second try block ends ");

      } catch (Exception e) {
        logger.info("Inside first Exception catch block ");

        throw new RuntimeException(e);
      }
      logger.info("first try block ends ");

    } catch (Exception e) {
      logger.info("Inside second Exception catch block ");

      logger.info("UserConsentManagementController testPDFDownload() - error ", e);
    }

    logger.exit("UserConsentManagementController testPDFDownload() - ends ");

    return new ResponseEntity<>(fileData, HttpStatus.OK);
  }
}
